package exercises;

public class NumbersDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 5;
		int y = 12;
		
		displayTwiceTheNumber(x, y);
	}
	
	public static void displayTwiceTheNumber(int x, int y) {
		
		int x2;
		int y2;
		
		x2 = x * 5;
		y2 = y * 5;
		System.out.println("x = " + x2 + " y = " + y2);
	}

}
